// @ts-nocheck
import { ActionConsoleData } from '../Output/Type/ActionConsoleData'
import { useCallback, useEffect, useReducer, useState } from 'react'
import { ActionOutputData } from '../Output/Type/ActionOutputData'
import { IConsoleData } from '../Output/Interface/IConsoleData'
import { IWriteOutput} from '../Output/Interface/IWriteOutput'
import { dataServer } from '../Server/Command/Repository'
import { serverHub } from '../Server/WebSoket/Hub'
import { Output } from '../Output/Output'
import { Input } from '../Input/Input'
import '../css/console.css'

const ConsoleDataReducer = (state : IConsoleData, action: ActionConsoleData) : IConsoleData => {
    switch (action.type) {
      case 'changeAllValue':
        return {directory: action.data.directory, history: action.data.history, subDirectory: action.data.subDirectory}
      default:
        throw new Error();
    }
}

const ConsoleOutputDataReducer=(state : IConsoleOutputDataReducer,action : ActionOutputData) => {

}

interface IConsoleOutputDataReducer{
    output: IWriteOutput[],
    isDisableWrite: boolean
}

export const Console : FC = () =>{
    const [output, setOutput] = useState<IWriteOutput>([])
    const [isDisabledWrite, setIsDisabledWrite] = useState(false)
    const [outputData,setOutputData] = useReducer(ConsoleOutputDataReducer,{output: [],isDisabledWrite: false})
    const [consoleData, dispatch] = useReducer(ConsoleDataReducer,{ directory: '', history: [], subDirectory: [] })
    
    useEffect(() => {
        const connectionToServer = async () : Promise<void> =>{
            serverHub.connectionToHubAsync()
            serverHub.sendCommand(getOutputHub)
            await getConsoleDataAsync()
        }
        
        connectionToServer()
    }, []) 

    const sendCommand = useCallback(async (text : string): Promise<void> => {
        await dataServer.postCommandAsync(text.trim())
        await getConsoleDataAsync()
    },[])

    const stopCommand = useCallback(async () : Promise<void> => {
        await dataServer.stopCommandAsync()
    },[])

    const getOutputHub = useCallback((dataHub) : void => {
        const line = dataHub.output as string
        const status = dataHub.status as number
        
        if(line !== ''){
            setIsDisabledWrite(status === 0? true : false)
            setOutput((output) => [...output,{status ,line}])
        }
    }, [setOutput, setIsDisabledWrite])

    const getConsoleDataAsync = useCallback(async () : Promise<void> => {
        const currnetDirectory = await dataServer.currnetDirectoryAsync()
        const subDirectory = await dataServer.subDirectoriesAsync()
        const history = await dataServer.getHistoryAsync()

        const directory = currnetDirectory.replaceAll('\"','')

        dispatch({type: 'changeAllValue', data: {directory, subDirectory, history}})
    }, [])

    return(
    <div className='console'>
        <Output output = {output}/>
        <Input 
            isDisabledWrite={isDisabledWrite} 
            sendCommand = {sendCommand}
            stopCommand = {stopCommand}
            serverDirectory={consoleData.directory}  
            subDirectory={consoleData.subDirectory}
            history = {consoleData.history}
        />
    </div>
    )
}
