import { useCallback } from "react"

export const allocateSubDirectory = useCallback((inputValue :string,currentDirectory:string): string => {
    const command = inputValue.replace(currentDirectory.replace('\\\\','\\'),' ').replace('\\',' ')
    return command
},[])

export const replaceToOneSeparation =useCallback((currentDirectory: string) => {
    if(currentDirectory === '')
        return ''

    const command = (currentDirectory + '\\>').replace('\\\\','\\').replace('\\\\','\\')
    return command
},[])
