import { KeyboardEvent, FC, Key, FormEvent, useCallback, useReducer } from 'react'
import { allocateSubDirectory,replaceToOneSeparation } from './textProcessing'
import { IInputReducer } from './Interface/IInputReducer'
import { ActionKind } from './Enum/ActionKind'
import { StatusKey } from './Enum/StatusKey'
import { IInput } from './Interface/IInput'
import { Action } from './Type/Action'
import React from 'react'

let indexCurrentDirectory = 0
let indexCurrentHistory = 0

const InputReducer = (state : IInputReducer, action : Action) : IInputReducer => {
    switch(action.type){
        case ActionKind.ChangeAllData:
            return {...state, valueInput: action.valueInput, valueUser: action.valueUser}
        case ActionKind.Clear:
            return {...state, valueInput: '', valueUser: ''}
        case ActionKind.ChangeInputValue:
            return {...state, valueInput: action.valueInput}
        default:
            throw new Error();
    }
}

export const Input : FC<IInput> =  ({ isDisabledWrite, sendCommand, stopCommand, serverDirectory ,subDirectory,history})  => {
    const [inputData,dispatch] = useReducer(InputReducer,{valueInput: '', valueUser:'', currentSubDirectory: ''})

    const eventHandler = (e: KeyboardEvent): Key => {
        checkStopWrite(e)

        switch (e.key) {
            case StatusKey.SendCommand:
                sendCommand(inputData.valueInput)
                dispatch({type: ActionKind.Clear})
                break

            case StatusKey.SwitchSubDirectory:
                e.preventDefault()
                switchSubDirectory()
                break

            case StatusKey.Up:
                e.preventDefault()
                callUpCommand()
                break

            case StatusKey.Down:
                e.preventDefault()
                callDownCommand()
                break

            case StatusKey.Cancel:
                stopCommand()
                break
        }
        
        return e.key
    }

    const switchSubDirectory = (): void => {
        indexCurrentDirectory++
        if (indexCurrentDirectory > subDirectory.length - 1) 
            indexCurrentDirectory = 0
        
        dispatch({ type: ActionKind.ChangeInputValue, valueInput: (inputData.valueUser + allocateSubDirectory(subDirectory[indexCurrentDirectory],serverDirectory))})
    }

    const callDownCommand = () : void => {
        indexCurrentHistory++
        if (indexCurrentHistory > history.length - 1) 
            indexCurrentHistory = 0
        
        dispatch({ type: ActionKind.ChangeInputValue, valueInput: history[indexCurrentHistory].textCommand})
    }

    const callUpCommand = () : void => {
        indexCurrentHistory--;
        if (indexCurrentHistory < 0) 
            indexCurrentHistory =  history.length - 1;
        
        dispatch({ type: ActionKind.ChangeInputValue, valueInput: history[indexCurrentHistory].textCommand})
    }
    
    const checkStopWrite = useCallback ((e: KeyboardEvent): string => {
        if(e.key === StatusKey.Cancel)
            return e.key
        
        return isDisabledWrite ? e.key = '' : e.key
    },[isDisabledWrite])

    const changeHandler = useCallback((e : FormEvent<HTMLInputElement>) : void => {
        if(isDisabledWrite)
            return;

        dispatch({ type: ActionKind.ChangeAllData, valueInput: e.currentTarget.value,valueUser:e.currentTarget.value })
    },[isDisabledWrite])
    
    const scrollAuto = (e : HTMLInputElement) : void => {
        e?.scrollIntoView(false)
    }

    return <div className='inputElement'>
                <div>{replaceToOneSeparation(serverDirectory)}</div>
                <input ref ={scrollAuto} value = {inputData.valueInput} onKeyDown = {eventHandler} onChange = {changeHandler} className='inputConsole'/>
            </div> 
}
