import { allocateSubDirectory,replaceToOneSeparation } from './textProcessing'
import { KeyboardEvent, FC, Key, FormEvent, useState, useEffect } from 'react'
import { StatusKey } from './Enum/StatusKey'
import { IInput } from './Interface/IInput'
import React from 'react'

let indexCurrentDirectory = 0
let indexCurrentHistory = 0

export const Input : FC<IInput> =  ({ isDisabledWrite, sendCommand, stopCommand, serverDirectory ,subDirectory,history})  => {
    const [valueUser,setValueUser] = useState('')
    const [valueInput,setValueInput] = useState('')
    const [currentSubDirectory,setCurrentSubDirectory] = useState('');
    const eventHandler = (e: KeyboardEvent): Key => {
        checkStopWrite(e)

        switch (e.key) {
            case StatusKey.SendCommand:
                sendCommand(valueInput)
                setValueInput('')
                break;

            case StatusKey.SwitchSubDirectory:
                e.preventDefault()
                switchSubDirectory()
                break;

            case StatusKey.Up:
                e.preventDefault()
                callUpCommand()
                break;

            case StatusKey.Down:
                e.preventDefault()
                callDownCommand()
                break;

            case StatusKey.Cancel:
                stopCommand()
                break;
        }
        return e.key
    }

    const switchSubDirectory = (): void => {
        indexCurrentDirectory++
        if (indexCurrentDirectory > subDirectory.length - 1) 
            indexCurrentDirectory = 0
        
        setCurrentSubDirectory(allocateSubDirectory(subDirectory[indexCurrentDirectory],serverDirectory))
        setValueInput(valueUser + currentSubDirectory)
    }

    const callDownCommand = () : void => {
        indexCurrentHistory++
        if (indexCurrentHistory > history.length - 1) 
            indexCurrentHistory = 0
        
        setValueInput(history[indexCurrentHistory].textCommand)
    }

    const callUpCommand = () : void => {
        indexCurrentHistory--;
        if (indexCurrentHistory < 0) 
            indexCurrentHistory =  history.length - 1;
        
        setValueInput(history[indexCurrentHistory].textCommand)
    }
    
    const checkStopWrite = (e: KeyboardEvent): string => {
        if(e.key === StatusKey.Cancel)
        return e.key
        
        return isDisabledWrite ? e.key = '' : e.key
    }

    const changeHandler = (e : FormEvent<HTMLInputElement>) : void => {
        if(!isDisabledWrite){
            setValueUser(e.currentTarget.value)
            setValueInput(valueUser)
        }
    }
    
    const scrollAuto = (e : HTMLInputElement) : void => {
        e?.scrollIntoView(false)
    }

    return <div className='inputElement'>
                <div>{replaceToOneSeparation(serverDirectory)}</div>
                <input ref ={scrollAuto} value = {valueInput} onKeyDown = {eventHandler} onChange = {changeHandler} className='inputConsole'/>
            </div> 
}
