// @ts-nocheck
import * as signalR from '@microsoft/signalr'
import { IDataHub } from '../../Console/Output/Interface/IDataHub'

interface Handler {
    (data : IDataHub): void
}
/*
* Класс для получения данных с Hub.
*/
class Hub {
    hub 
    /*
    * Функция подключения и запуска signalR.
    */
    connectionToHubAsync = async () =>{
         this.hub = new signalR.HubConnectionBuilder() 
            .withUrl('https://localhost:7145/chat', {skipNegotiation: true, transport: signalR.HttpTransportType.WebSockets})
            .build()

            this.hub.start() 
    }
    /*
    * Функция для получения данных с Hub.
    * @param handle обработчик данных с hub.
    */
    getDataFromHub = (handle : Handler) =>{
        if(this.hub === null)
            return

        this.hub.on('Send', handle)
    }

    sendCommand = (command : string) : Promise<void>=> this.hub.on('AddCommand',command)
    
    stopProcessingCommand = () : Promise<void> => this.hub.on('Stop')

    getCurrentDirectory = () : Promise<string[]> => this.hub.on('GetDirectory')

    getSubDirectories = () : Promise<string[]> => this.hub.on('GetDirectories')

}

export const serverHub = new Hub()
