import { useNavigate } from 'react-router-dom'
import React, { useReducer, useRef } from 'react'
import { User } from '../Type/User'
import '../../css/auth.css'

type ReducerNewState = {
    type: string,
    value: string,
}

const Reducer = (state : User,newState : ReducerNewState) : User => {
    switch(newState.type){
        case 'login':
            return {login: newState.value, password: state.password}
        case 'password':
            return {login: state.login, password: newState.value}
        default:
            return state
    }
}

const initalReducer : User = {
    login: '',
    password: ''
}

export const Login = () => {
    const navigate = useNavigate()
    const [valueUser,dispatch] = useReducer(Reducer,initalReducer)
    const passwordValue = useRef<HTMLInputElement>(null)
    const loginValue = useRef<HTMLInputElement>(null)

    const authorization = async (data : User) : Promise<void> => {
        const token = await getToken(data)
        console.log(data);
        if(token === '' || token === null)
            return 

    }

    const getToken = async (data : User) : Promise<string> => {

        const urlLogin = 'https://localhost:7145/Jwt/Login'
        const responce = await fetch(urlLogin,{ method: 'POST',
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
            'body':  JSON.stringify(data)
        }})
    
        const promise = await responce.text()
        return promise
    }
    //navigate('/console')
    return (
        <div className='flex panel'>
            <h1>WebShell</h1>
            <div className='lineTop'/>
            <label>Логин</label>
            <input ref={loginValue} className = 'input' />
            <label>Пароль</label>
            <input ref={passwordValue} className = 'input' />
            <div className='lineBottom'/>
            <button className='button login' onClick={()=>{authorization({login: loginValue.current?.value,password: passwordValue.current?.value})}}>Отправить </button>
            <button className='button register'  onClick={() => window.location.href='http://localhost:3000/register'}>Регистрация</button>
        </div>
    );
}
