import { TokenContext } from '../AuthorizationProvider'
import React, { useContext, useRef } from 'react'
import { useNavigate } from 'react-router-dom'
import { CookiePanel } from './CookiePanel'
import '../../css/auth.css'
import 'ts-replace-all'

type User = {
    Id:number,
    Login:string | undefined,
    Password:string | undefined
}

type ClientData = {
    User: User,
    isJWT: boolean
}
/*
 *  Функциональный компонент входа в систему.
 */
export const Login = () => {
    const navigate = useNavigate()
    const passwordValue = useRef<HTMLInputElement>(null)
    const loginValue = useRef<HTMLInputElement>(null)
    const tokenContext = useContext(TokenContext)
    
   /*
    *  Отправка данных на сервер.
    */
    const authorization = async (data : User) : Promise<void> => {

        const urlLogin = 'https://localhost:7145/auth/login'
        const isJWT = tokenContext.isJWT;
        const clientData : ClientData = {User: data, isJWT:isJWT}

        const responce = await fetch (urlLogin,{ 
            method: 'POST',
            body:  JSON.stringify(clientData),
            headers: {'Content-Type': 'application/json'}
        })

        const promise = await responce.text()

        if(isJWT){
            console.log('Jwt: '+ promise)
            tokenContext.setToken(promise)
        }
        else 
        {
            console.log('Cookie: '+ promise)
            tokenContext.setToken(document.cookie)
        }

        if(tokenContext.token.length > 10)
            navigate('/console')
    }
    
    return (
        <div>
            <div className='flex panel'>
                <h1>WebShell</h1>
                <div className='lineTop'/>
                <label>Логин</label>
                <input ref={loginValue} className = 'input' />
                <label>Пароль</label>
                <input ref={passwordValue} className = 'input' />
                <div className='lineBottom'/>
                <button className='button login' onClick={()=>{authorization({Id: 0, Login: loginValue.current?.value,Password: passwordValue.current?.value})}}>Отправить </button>
                <button className='button register' onClick={() => window.location.href='/register'}>Регистрация</button>
            </div>
            <CookiePanel />
        </div>
    )
}
