import { IHistory } from '../../Console/Input/Interface/IHistory'
import 'ts-replace-all'

type HeaderRequest = {
    GET: () => RequestInit,
    POST: (dataClient : any) => RequestInit
}

const initialHeader : HeaderRequest= {
    GET:() : RequestInit=> {return {}},
    POST(dataClient) {
        return dataClient
    },
}

class Repository {
    token = ''
    header = initialHeader
    headerJWT : HeaderRequest = { 
        GET: () : RequestInit => {
            return {
                method: 'GET',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': "Bearer " + this.token
                },
            }
        },
        POST: (dataClient : any) : RequestInit => {
            return { 
                method: 'POST',
                redirect: 'follow',
                body: JSON.stringify(dataClient),
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': "Bearer " + this.token
                },
            }
        } 
    }

    headerCookie : HeaderRequest = { 
        GET: {
            method: 'GET',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
            credentials: 'include'
        },
        POST: (dataClient : any) : RequestInit => {
          return { 
                method: 'POST',
                redirect: 'follow',
                body: JSON.stringify(dataClient),
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json',
                },
                credentials: 'include'
            }
        } 
    }

    constructor(token : string,isJWT :boolean){
       this.token = token.replaceAll('\"','')
       if(isJWT)
            this.header = this.headerJWT;
        else
            this.header = this.headerCookie;
    }

    postCommandAsync = async (command : string) : Promise<void> => {
        const urlAddOnServer = 'https://localhost:7145/command/add'
        const dataClient = { id: 0, data: '', textCommand: command }
        if (command !== '') {
            await fetch(urlAddOnServer, this.header.POST(dataClient))
        }
    }

    currnetDirectoryAsync = async () : Promise<string> => {
        const urlGetDirectory = 'https://localhost:7145/command/getDirectory'
        const responce = await fetch(urlGetDirectory, this.header.GET);
        
        return await responce.text()
    }
    
    subDirectoriesAsync = async () : Promise<string[]> => {
        const urlGetDirectories = 'https://localhost:7145/command/getDirectories'
        const responce = await fetch(urlGetDirectories,this.header.GET)

        const responceJson = await responce.json()
        const result = await responceJson as string[]

        return result
    }

    getHistoryAsync = async () : Promise<IHistory[]> => {
        const urlGetHistory = 'https://localhost:7145/command/getHistory'
        const response = await fetch(urlGetHistory,this.header.GET)
        const responceJson = await response.json()
        const result = await responceJson as IHistory[]

        return result
    }

    stopCommandAsync = async () : Promise<void> => {

        const urlStopCommand = 'https://localhost:7145/command/Stop'
        await fetch(urlStopCommand, this.header.POST(null))
    }
}

export const instanceRepository = (token : string,isJWT : boolean) : Repository=>  {
    return new Repository(token,isJWT)
}
