// @ts-nocheck
import { IHistory } from '../../Console/Input/Interface/IHistory'
import { IDataHub } from '../../Console/Output/Interface/IDataHub'
import * as signalR from '@microsoft/signalr'

interface Handler {
    (data : IDataHub): void
}
/*
* Класс для получения данных с Hub.
*/
class Hub {
    hub 
    /*
    * Функция подключения и запуска signalR.
    */
    connectionToHubAsync = async (isJWT:bool,token : string) =>{
        this.InitHub(isJWT,token)
        this.hub.start()
    }

    /*
    * Функция для получения данных с Hub.
    * @param isJWT выбор поставщика токена.
    * @param token токен доступа.
    */
    InitHub = (isJWT: boolean, token : string) => { 
        if(isJWT){
            this.hub = new signalR.HubConnectionBuilder() 
            .withUrl('https://localhost:7145/chat', {headers: "Bearer: " + token, skipNegotiation: true, transport: signalR.HttpTransportType.WebSockets})
            .build()
        }
        else{
            this.hub = new signalR.HubConnectionBuilder() 
            .withUrl('https://localhost:7145/chat', {headers: "Cookie " + token, skipNegotiation: true, transport: signalR.HttpTransportType.WebSockets})
            .build()
        }
    }

   /*
    * Функция для получения данных с Hub.
    * @param handle обработчик данных с hub.
    */
    getDataFromHub = (handle : Handler) =>{
        if(this.hub === null)
            return

        this.hub.on('Send', handle)
    }
    
   /*
    * Функция для отправки комманды на сервер.
    * @param command команда пользователя.
    */
    sendCommand = (command : string) : Promise<void> => this.hub.invoke('AddCommand',command)
   /*
    * Функция останавливающая выполняюмую команду на сервере ранее введенная пользователем.
    */
    stopProcessingCommand = () : Promise<void> => this.hub.invoke('Stop')
   /*
    * Функция получающая от сервера текущую директорию
    */
    getCurrentDirectory = () : Promise<string> => this.hub.invoke('GetDirectory')
   /*
    * Функция получающая дочерние директории.
    */
    getSubDirectories = () : Promise<string[]> => this.hub.invoke('GetDirectories')
 /*
    * Функция получающая историю комманд введеных пользователем.
    */
    getHistoryAsync = () : Promise<IHistory[]> => this.hub.invoke('GetHistory')
}

export const serverHub = new Hub()
