// @ts-nocheck
import { IConsoleData } from '../Output/Interface/IConsoleData'
import { IWriteOutput} from '../Output/Interface/IWriteOutput'
import { dataServer } from '../Server/Command/Repository'
import { useCallback, useEffect, useState } from 'react'
import { serverHub } from '../Server/WebSoket/Hub'
import { Output } from '../Output/Output'
import { Input } from '../Input/Input'
import '../css/console/console.css'

export const Console = () =>{
    const [output, setOutput] = useState<IWriteOutput>([])
    const [isDisabledWrite, setIsDisabledWrite] = useState(false)
    const [consoleData, setConsoleData] = useState<IConsoleData>({
        directory: '',
        history: [],
        subDirectory: []
    })
    
    useEffect(() => {
        const connectionToServer = async () : Promise<void> =>{
            serverHub.connectionToHubAsync()
            serverHub.sendCommand(getOutputHub)
            await getConsoleDataAsync()
        }
        
        connectionToServer()
    }, [])

    const sendCommand = async (text : string): Promise<void> => {
        dataServer.postCommandAsync(text.trim())
        await getConsoleDataAsync()
    }

    const stopCommand = useCallback(async () : Promise<void> => {
        await dataServer.stopCommandAsync()
    },[])

    const getOutputHub = (dataHub) : void => {
        const line = dataHub.output as string
        const status = dataHub.status as number
        
        if(line !== ''){
            setIsDisabledWrite(status === 0? true : false)
            setOutput((output) => [...output,{status ,line}])
        }
    }

    const getConsoleDataAsync = async () : Promise<void> => {
        const currnetDirectory = await dataServer.currnetDirectoryAsync()
        const subDirectory = await dataServer.subDirectoriesAsync()
        const history = await dataServer.getHistoryAsync()
            
        setConsoleData({
            directory: currnetDirectory.replaceAll('"', ''),
            subDirectory,
            history
        })
    }

    return(
    <div className='console'>
        <Output output = {output}/>
        <Input 
            isDisabledWrite={isDisabledWrite} 
            sendCommand = {sendCommand}
            stopCommand = {stopCommand}
            serverDirectory={consoleData.directory}  
            subDirectory={consoleData.subDirectory}
            history = {consoleData.history}
        />
    </div>
    )
}
